import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';

// Initialize Supabase client
const supabaseUrl = 'https://zlldvwdafeytxvorhtqo.supabase.co';
const supabaseKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6InpsbGR2d2RhZmV5dHh2b3JodHFvIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MTM3MjY2MTUsImV4cCI6MjAyOTMwMjYxNX0.7So7AXX8oFNwtDIkob3QQjwrlWIX6loBqgIIWouj1JI'
const supabase = createClient(supabaseUrl, supabaseKey);

function Activity() {
  const [activity, setActivity] = useState('Click a button to get an activity!');
  const [car, setCar] = useState(null);

  const fetchActivity = async (participants = 1) => {
    try {
      const response = await fetch(`https://www.boredapi.com/api/activity?participants=${participants}`);
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
      const data = await response.json();
      setActivity(data.activity);
    } catch (err) {
      console.error('Error fetching data: ', err);
      setActivity('Failed to fetch new activity. Please try again!');
    }
  };

  const suggestCar = async () => {
    try {
      let { data, error } = await supabase
        .from('cars')
        .select('*')
        .order('id', { ascending: true })
        .limit(1);

      if (error) throw error;
      setCar(data[0]); // Assuming 'cars' table has records and 'data' is an array
    } catch (error) {
      console.error('Error fetching car data: ', error);
      setCar({ name: 'No car suggestions available at the moment.' });
    }
  };

  return (
    <div className="p-10 bg-green-200">
      <div className="max-w-xl mx-auto bg-green-800 text-white rounded-lg shadow-xl p-5 mb-10">
        <h2 className="text-2xl font-bold mb-4">Solo Activity</h2>
        <p>{activity}</p>
        <button onClick={() => fetchActivity(1)} className="bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-6 rounded mt-4">
          Get Solo Activity
        </button>
      </div>

      <div className="max-w-xl mx-auto bg-green-800 text-white rounded-lg shadow-xl p-5">
        <h2 className="text-2xl font-bold mb-4">Group Activity</h2>
        <p>{activity}</p>
        <button onClick={() => fetchActivity(2)} className="bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-6 rounded mt-4">
          Get Group Activity
        </button>
        {car && <p className="mt-4">Suggested Car: {car.name}</p>}
        <button onClick={suggestCar} className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded mt-4">
          Suggest a Car
        </button>
      </div>
    </div>
  );
}

export default Activity;
