import React, { useState } from 'react';

function Activity() {
  const [soloActivity, setSoloActivity] = useState('Click the button to get a solo activity!');
  const [groupActivity, setGroupActivity] = useState('Click the button to get a group activity!');

  const fetchActivity = (setActivityFunction, participants) => {
    fetch(`https://www.boredapi.com/api/activity?participants=${participants}`)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setActivityFunction(data.activity || 'No activity found');
      })
      .catch(err => {
        console.error('Error fetching data: ', err);
        setActivityFunction('Failed to fetch new activity. Please try again!');
      });
  };

  return (
    <div className="p-10 max-w-4xl mx-auto space-y-8">
      <div className="bg-green-800 text-white rounded-lg shadow-xl p-5">
        <h1 className="text-2xl font-bold mb-4">Solo Activity</h1>
        <p><strong>Activity:</strong> {soloActivity}</p>
        <button onClick={() => fetchActivity(setSoloActivity, 1)} className="bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-6 rounded mt-4">
          Generate Solo Activity
        </button>
      </div>

      <div className="bg-green-800 text-white rounded-lg shadow-xl p-5">
        <h1 className="text-2xl font-bold mb-4">Group Activity</h1>
        <p><strong>Activity:</strong> {groupActivity}</p>
        <button onClick={() => fetchActivity(setGroupActivity, 2)} className="bg-green-500 hover:bg-green-600 text-white font-bold py-3 px-6 rounded mt-4">
          Generate Group Activity
        </button>
      </div>
    </div>
  );
}

export default Activity;
